<?php

namespace Modules\Infrastructure\Tests\Unit\Utilities;

use Modules\Infrastructure\Exceptions\DecodeFileBase64FailedException;
use Modules\Infrastructure\Utilities\Base64FileDecoder;
use Tests\TestCase;

class Base64FileDecoderTest extends TestCase
{
    private $base64FileDecoder;

    public function setUp(): void
    {
        parent::setUp();
        $this->base64FileDecoder = new Base64FileDecoder();
    }

    /**
     * @test
     * @dataProvider base64_files_provider
     * @param string $data
     * @param array $return_structure
     */
    public function test_decode_base64_file_succefully($data, $structure)
    {
        $result = $this->base64FileDecoder->decodeBase64File($data);
        $this->assertTrue(count(array_diff_key($result, $structure)) === 0);
        $this->assertEquals($result["extension"], $structure["extension"]);
        $this->assertNotEmpty($result["body"]);
    }

    public function test_decode_base64_file_failed_will_throw_exception()
    {
        $this->withoutExceptionHandling();
        $message = (trans('messages.invalid_file'));
        $this->expectException(DecodeFileBase64FailedException::class);
        $this->expectExceptionMessage($message);

        $encodedFile = $this->getBase64EncodedFile();
        $encodedFile = str_replace("image/jpeg;base64", '', $encodedFile);
        $this->base64FileDecoder->decodeBase64File($encodedFile);
    }

    public function getBase64EncodedFile($type = '')
    {
        $this->base64FileDecoder = new Base64FileDecoder();

        $base64Content = 'data:image/jpeg;base64,';
        switch ($type) {

            case 'xlsx':
                $base64Content = "data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;base64,";
                break;

            case 'pdf':
                $base64Content = "data:application/pdf;base64,";
                break;

        }
        return $base64Content;
    }

    public function base64_files_provider()
    {
        return [
            'decode_image_should_success' => [
                'data' => $this->getBase64EncodedFile('jpeg'),
                'return_structure' => ["body" => "", "extension" => "jpeg"],
            ],
            'decode_xlsx_file_should_success' => [
                'data' => $this->getBase64EncodedFile('xlsx'),
                'return_structure' => ["body" => "", "extension" => "xlsx"],
            ],
            'request_should_pass_when_all_inputs_are_valid' => [
                'data' => $this->getBase64EncodedFile('pdf'),
                'return_structure' => ["body" => "", "extension" => "pdf"],
            ],

        ];
    }
}
